import React, { useEffect, useState, useRef } from "react";

function Elimination() {
  const [users, setUsers] = useState([]);
  const audioRefs = useRef({});

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await fetch("https://ybg-back.vercel.app/elimination");
        const data = await response.json();
        setUsers(data.users);
      } catch (error) {
        console.error("Error fetching users:", error);
      }
    };

    fetchUsers();
  }, []);

  const renderDiamondGrid = () => {
    const sortedUsers = [...users].sort((a, b) => a.player_number - b.player_number);

    return (
      <div className="relative w-full min-h-screen flex items-center justify-center flex-wrap">
        {sortedUsers.map((user, index) => (
          <div
            key={user.id}
            className="relative w-24 h-24 m-2 bg-cover bg-center flex items-center justify-center"
            style={{
              backgroundImage: user.photo
                ? `url(https://ybg-back.vercel.app/user/${user.id}/photo)`
                : "none",
              transform: "rotate(45deg)",
              border: "2px solid #00FF00",
            }}
          >
            {user.audio && (
              <audio
                ref={(el) => (audioRefs.current[user.id] = el)}
                src={`https://ybg-back.vercel.app/user/${user.id}/audio`}
              />
            )}
            <div
              className="absolute transform -rotate-45 bg-black text-green-500 text-xl font-digital w-full text-center"
              style={{
                bottom: "-10px",
                left: 0,
                fontFamily: "Digital",
              }}
            >
              {user.player_number.toString().padStart(3, "0")}
            </div>
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="bg-black min-h-screen text-white">
      <h1 className="text-4xl text-green-500 text-center mb-8 uppercase tracking-widest">
        Elimination Board
      </h1>
      {renderDiamondGrid()}
      <style jsx>{`
        @font-face {
          font-family: "Digital";
          src: url("/fonts/Digital7.ttf") format("truetype");
        }
      `}</style>
    </div>
  );
}

export default Elimination;
