import React, { useEffect, useState, useRef } from "react";

function Elimination() {
  const [users, setUsers] = useState([]);
  const audioRefs = useRef({});

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await fetch("https://ybg-back.vercel.app/elimination");
        const data = await response.json();
        setUsers(data.users);
      } catch (error) {
        console.error("Error fetching users:", error);
      }
    };

    fetchUsers();
  }, []);

  const toggleElimination = (user) => {
    const audioElement = audioRefs.current[user.id];
    if (audioElement) {
      audioElement.play();
    }
  };

  const renderDiamondGrid = () => {
    const sortedUsers = [...users].sort((a, b) => a.player_number - b.player_number);

    return (
      <div className="flex flex-wrap justify-center gap-4">
        {sortedUsers.map((user) => (
          <div
            key={user.id}
            className="relative w-32 h-32 transform rotate-45 bg-pink-600 overflow-hidden"
            style={{
              clipPath: "polygon(50% 0%, 100% 50%, 50% 100%, 0% 50%)",
            }}
            onClick={() => toggleElimination(user)}
          >
            {/* Photo Container */}
            <div
              className="absolute inset-0 transform -rotate-45"
              style={{
                backgroundImage: `url(https://ybg-back.vercel.app/user/${user.id}/photo)`,
                backgroundSize: "cover",
                backgroundPosition: "center",
              }}
            ></div>

            {/* Player Number */}
            <div
              className="absolute bottom-0 left-0 w-full text-center text-cyan-400 text-xl font-bold"
              style={{
                transform: "rotate(-45deg)",
                textShadow: "0 0 10px rgba(0, 255, 255, 0.7)",
              }}
            >
              {user.player_number.toString().padStart(3, "0")}
            </div>

            {/* Audio Element */}
            {user.audio && (
              <audio ref={(el) => (audioRefs.current[user.id] = el)} src={`https://ybg-back.vercel.app/user/${user.id}/audio`} />
            )}
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="bg-black min-h-screen text-white flex flex-col items-center">
      <h1 className="text-4xl text-red-500 text-center mb-8 uppercase tracking-widest">
        Elimination Board
      </h1>
      {renderDiamondGrid()}
    </div>
  );
}

export default Elimination;
