import React, { useState } from 'react';
import { motion } from 'framer-motion';

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    surname: ''
  });
  const [isLoading, setIsLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    
    try {
      const response = await fetch("https://ybg-back.vercel.app/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      const data = await response.json();
      alert(data.message);
    } catch (error) {
      console.error("Error:", error);
      alert("Failed to register");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-black flex flex-col items-center justify-center p-4">
      {/* Decorative circles inspired by Squid Game shapes */}
      <div className="absolute top-8 left-8">
        <div className="w-16 h-16 border-4 border-pink-600 rounded-full opacity-50" />
      </div>
      <div className="absolute bottom-8 right-8">
        <div className="w-16 h-16 border-4 border-pink-600 transform rotate-45 opacity-50" />
      </div>
      
      <motion.div 
        initial={{ scale: 0.9, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ duration: 0.5 }}
        className="w-full max-w-md"
      >
        <h1 className="text-5xl md:text-6xl text-center mb-12 text-pink-600 font-bold tracking-wider">
          YOUR BEST GAME
        </h1>

        <motion.form 
          onSubmit={handleSubmit}
          className="space-y-6 bg-gray-900 p-8 rounded-lg border-2 border-pink-600"
        >
          <div className="space-y-4">
            <motion.div
              whileFocus={{ scale: 1.02 }}
              className="relative"
            >
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                className="w-full px-4 py-3 bg-gray-800 text-white border-2 border-pink-600 rounded-lg focus:outline-none focus:border-pink-400 transition-colors"
                placeholder="Enter your name"
                required
              />
            </motion.div>

            <motion.div
              whileFocus={{ scale: 1.02 }}
              className="relative"
            >
              <input
                type="text"
                name="surname"
                value={formData.surname}
                onChange={handleChange}
                className="w-full px-4 py-3 bg-gray-800 text-white border-2 border-pink-600 rounded-lg focus:outline-none focus:border-pink-400 transition-colors"
                placeholder="Enter your surname"
                required
              />
            </motion.div>
          </div>

          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            type="submit"
            disabled={isLoading}
            className={`w-full py-4 rounded-lg text-xl font-bold text-white 
              ${isLoading ? 'bg-pink-800' : 'bg-pink-600 hover:bg-pink-500'} 
              transition-colors duration-300 relative overflow-hidden`}
          >
            {isLoading ? (
              <div className="flex items-center justify-center">
                <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-white"/>
              </div>
            ) : (
              'ENTER THE GAME'
            )}
          </motion.button>
        </motion.form>
      </motion.div>
    </div>
  );
};

export default Register;
