import React, { useState } from "react";
import "../fonts.css";

function Register() {
  const [name, setName] = useState("");
  const [surname, setSurname] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:5000/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, surname }),
      });
      const data = await response.json();
      alert(data.message);
    } catch (error) {
      console.error("Error:", error);
      alert("Failed to register");
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>YOUR BEST GAME</h1>
      <form onSubmit={handleSubmit} style={styles.form}>
        <input
          type="text"
          placeholder="Enter your name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={styles.input}
          required
        />
        <input
          type="text"
          placeholder="Enter your surname"
          value={surname}
          onChange={(e) => setSurname(e.target.value)}
          style={styles.input}
          required
        />
        <button type="submit" style={styles.button}>
          Register
        </button>
      </form>
      <div style={styles.footer}>
        <p style={styles.footerText}>Will you survive the game?</p>
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    height: "100vh",
    backgroundColor: "#101010", // Darker background for Squid Game theme
    color: "#fff",
    fontFamily: "'SquidGameFont', sans-serif",
    position: "relative",
    overflow: "hidden",
  },
  title: {
    fontSize: "4rem",
    color: "#ff007f", // Squid Game pink
    textShadow: "4px 4px 10px rgba(255, 0, 127, 0.8), 0 0 20px rgba(255, 0, 127, 0.6)",
    marginBottom: "3rem",
  },
  form: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    gap: "1.5rem",
    width: "90%",
    maxWidth: "400px",
    backgroundColor: "rgba(0, 0, 0, 0.7)",
    padding: "20px",
    borderRadius: "10px",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.5)",
  },
  input: {
    width: "100%",
    padding: "12px",
    fontSize: "1.1rem",
    borderRadius: "5px",
    border: "2px solid #ff007f",
    outline: "none",
    backgroundColor: "#333",
    color: "#fff",
    fontFamily: "'SquidGameFont', sans-serif",
    transition: "all 0.3s ease",
  },
  inputFocus: {
    borderColor: "#e60072",
  },
  button: {
    width: "100%",
    padding: "12px",
    fontSize: "1.5rem",
    borderRadius: "5px",
    border: "none",
    backgroundColor: "#ff007f",
    color: "#fff",
    fontFamily: "'SquidGameFont', sans-serif",
    cursor: "pointer",
    textTransform: "uppercase",
    transition: "all 0.3s ease",
  },
  buttonHover: {
    backgroundColor: "#e60072",
  },
  footer: {
    position: "absolute",
    bottom: "20px",
    textAlign: "center",
  },
  footerText: {
    fontSize: "1.2rem",
    color: "#fff",
    textShadow: "0 0 10px rgba(255, 255, 255, 0.8)",
  },
};

export default Register;
