import React, { useEffect, useState, useRef } from "react";
import { calculateDiamondPosition } from "/";

function Elimination() {
  const [users, setUsers] = useState([]);
  const audioRefs = useRef({});
  const [eliminatedUsers, setEliminatedUsers] = useState({});

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await fetch("https://ybg-back.vercel.app/elimination");
        const data = await response.json();
        setUsers(data.users);
      } catch (error) {
        console.error("Error fetching users:", error);
      }
    };

    fetchUsers();
  }, []);

  const toggleElimination = (user) => {
    setEliminatedUsers((prev) => ({
      ...prev,
      [user.id]: !prev[user.id],
    }));

    const audioElement = audioRefs.current[user.id];
    if (audioElement) {
      if (!eliminatedUsers[user.id]) {
        audioElement.play();
      } else {
        audioElement.pause();
        audioElement.currentTime = 0;
      }
    }
  };

  const renderDiamondGrid = () => {
    const sortedUsers = [...users].sort((a, b) => a.player_number - b.player_number);

    return (
      <div className="relative w-full h-[80vh] flex items-center justify-center">
        {sortedUsers.map((user) => (
          <div
            key={user.id}
            className={`absolute transform rotate-45 w-32 h-32 bg-cover bg-center cursor-pointer transition-all duration-300 ease-in-out ${
              eliminatedUsers[user.id] ? 'opacity-50' : 'opacity-100'
            }`}
            style={{
              top: calculateDiamondPosition(user.player_number, sortedUsers.length).top,
              left: calculateDiamondPosition(user.player_number, sortedUsers.length).left,
            }}
            onClick={() => toggleElimination(user)}
          >
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 400 400">
              <defs>
                <clipPath id="diamond">
                  <polygon points="200,50 350,200 200,350 50,200" />
                </clipPath>
              </defs>
              
              <image x="50" y="50" width="300" height="300" preserveAspectRatio="xMidYMid slice"
                     href={`https://ybg-back.vercel.app/user/${user.id}/photo`} />
              
              <text x="200" y="200" font-size="100" font-weight="bold" text-anchor="middle" fill="#00F5FF" dominant-baseline="middle">
                {user.player_number}
              </text>
            </svg>
            {user.audio && (
              <audio
                ref={(el) => (audioRefs.current[user.id] = el)}
                src={`https://ybg-back.vercel.app/user/${user.id}/audio`}
              />
            )}
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="bg-black min-h-screen text-white p-8">
      <h1 className="text-4xl text-red-500 text-center mb-8 uppercase tracking-widest">
        Elimination Board
      </h1>
      {renderDiamondGrid()}
    </div>
  );
}

export default Elimination;
