export const calculateDiamondPosition = (playerNumber, total) => {
    const radius = 150;
    const angle = (Math.PI * 2 * (playerNumber - 1)) / total - Math.PI / 2;
    return {
      top: `calc(50% + ${radius * Math.sin(angle)}px - 64px)`,
      left: `calc(50% + ${radius * Math.cos(angle)}px - 64px)`,
    };
  };
