import React, { useEffect, useState, useRef } from "react";
import sound from './sound.mp3';

<audio ref={commonSoundRef} src={sound} />

function Elimination() {
  const [users, setUsers] = useState([]);
  const audioRefs = useRef({});
  const [eliminatedUsers, setEliminatedUsers] = useState({});
  const commonSoundRef = useRef(null); // Reference for the common sound

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await fetch("https://ybg-back.vercel.app/elimination");
        const data = await response.json();
        setUsers(data.users);
      } catch (error) {
        console.error("Error fetching users:", error);
      }
    };

    fetchUsers();
  }, []);

  const toggleElimination = (user) => {
    setEliminatedUsers((prev) => ({
      ...prev,
      [user.id]: !prev[user.id],
    }));

    // Play user-specific audio
    const userAudioElement = audioRefs.current[user.id];
    if (userAudioElement) {
      if (!eliminatedUsers[user.id]) {
        userAudioElement.play();
      } else {
        userAudioElement.pause();
        userAudioElement.currentTime = 0;
      }
    }

    // Play the common sound
    if (!eliminatedUsers[user.id] && commonSoundRef.current) {
      commonSoundRef.current.play();
    }
  };

  const renderDiamondGrid = () => {
    const sortedUsers = [...users].sort(
      (a, b) => a.player_number - b.player_number
    );

    return (
      <div className="relative w-full h-[80vh] flex items-center justify-center">
        {sortedUsers.map((user) => (
          <div
            key={user.id}
            className={`relative w-32 h-32 bg-cover bg-center cursor-pointer transition-all duration-300 ease-in-out ${
              eliminatedUsers[user.id] ? "opacity-50" : "opacity-100"
            }`}
            style={{
              clipPath: "polygon(50% 0, 100% 50%, 50% 100%, 0 50%)",
              filter: "grayscale(20%) brightness(0.9) sepia(10%) hue-rotate(-20deg)", // Pink effect
            }}
            onClick={() => toggleElimination(user)}
          >
            <img
              src={`https://ybg-back.vercel.app/user/${user.id}/photo`}
              alt={`Player ${user.player_number}`}
              className="w-full h-full object-cover"
              style={{
                filter: "contrast(1.2) brightness(0.8)", // TV effect
              }}
            />
            {/* Player Number */}
            <div
              className="absolute"
              style={{
                bottom: "15%", // 20% from the bottom of the diamond
                left: "50%", // Center horizontally
                transform: "translate(-50%, 0)", // Adjust to ensure centered alignment
                fontSize: "0.9rem", // Smaller text
                color: "#00FF00", // Green color
                textShadow:
                  "0 0 8px rgba(0, 255, 0, 0.8), 0 0 12px rgba(0, 255, 0, 0.6)", // Glow effect
                fontWeight: "bold", // Ensure visibility
                fontFamily: "Digital",
              }}
            >
              {String(user.player_number).padStart(3, "0")}
            </div>
            {user.audio && (
              <audio
                ref={(el) => (audioRefs.current[user.id] = el)}
                src={`https://ybg-back.vercel.app/user/${user.id}/audio`}
              />
            )}
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="bg-black min-h-screen text-white p-8">
      <h1 className="text-4xl text-red-500 text-center mb-8 uppercase tracking-widest">
        Elimination Board
      </h1>
      {renderDiamondGrid()}
      {/* Add the common sound element */}
      <audio ref={commonSoundRef} src="../sound.MP3" />
    </div>
  );
}

export default Elimination;
