import React, { useEffect, useState } from "react";

function Elimination() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await fetch("https://ybg-back.vercel.app/elimination");
        const data = await response.json();
        setUsers(data.users);
      } catch (error) {
        console.error("Error fetching users:", error);
      }
    };

    fetchUsers();
  }, []);

  const renderDiamondGrid = () => {
    const sortedUsers = [...users].sort((a, b) => a.player_number - b.player_number);

    return (
      <div className="grid justify-center gap-2 relative" style={{ maxWidth: "800px" }}>
        {sortedUsers.map((user, index) => (
          <div
            key={user.id}
            className="relative flex items-center justify-center"
            style={{
              width: "100px",
              height: "100px",
              clipPath: "polygon(50% 0%, 100% 50%, 50% 100%, 0% 50%)",
              background: `url(https://ybg-back.vercel.app/user/${user.id}/photo) no-repeat center center`,
              backgroundSize: "cover",
              transform: "rotate(45deg)",
            }}
          >
            <div
              className="absolute inset-0 flex items-center justify-center text-green-500 text-lg font-digital"
              style={{
                transform: "rotate(-45deg)",
                textShadow: "0 0 8px rgba(0, 255, 0, 0.8)",
              }}
            >
              {user.player_number.toString().padStart(3, "0")}
            </div>
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="bg-black min-h-screen text-white flex flex-col items-center justify-center">
      <h1 className="text-4xl text-red-500 uppercase tracking-widest mb-8">
        Elimination Board
      </h1>
      {renderDiamondGrid()}
      <style jsx>{`
        @font-face {
          font-family: "Digital";
          src: url("/fonts/Digital7.ttf") format("truetype");
        }
        .font-digital {
          font-family: "Digital", monospace;
        }
      `}</style>
    </div>
  );
}

export default Elimination;
