import React, { useEffect, useState, useRef } from "react";

function Elimination() {
  const [users, setUsers] = useState([]);
  const audioRefs = useRef({});

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await fetch("https://ybg-back.vercel.app/elimination");
        const data = await response.json();
        setUsers(data.users);
      } catch (error) {
        console.error("Error fetching users:", error);
      }
    };

    fetchUsers();
  }, []);

  const renderDiamondGrid = () => {
    const sortedUsers = [...users].sort((a, b) => a.player_number - b.player_number);

    return (
      <div className="relative w-full min-h-screen flex items-center justify-center flex-wrap">
        {sortedUsers.map((user, index) => (
          <div
            key={user.id}
            className="relative w-24 h-24 m-2 bg-pink-500 flex items-center justify-center cursor-pointer"
            style={{
              clipPath: "polygon(50% 0%, 100% 50%, 50% 100%, 0% 50%)", // Diamond shape
              transform: "rotate(45deg)", // Rotated for the diamond effect
              background: user.photo
                ? `url(https://ybg-back.vercel.app/user/${user.id}/photo) no-repeat center center`
                : "pink",
              backgroundSize: "cover",
            }}
          >
            <div
              className="absolute -rotate-45 bg-black text-green-500 text-sm font-digital px-2 py-1 rounded-full"
              style={{
                bottom: "-10px",
                left: "50%",
                transform: "translateX(-50%)",
              }}
            >
              {user.player_number.toString().padStart(3, "0")}
            </div>
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="bg-black min-h-screen text-white p-8">
      <h1 className="text-4xl text-red-500 text-center mb-8 uppercase tracking-widest">
        Elimination Board
      </h1>
      {renderDiamondGrid()}
      <style jsx>{`
        @font-face {
          font-family: "Digital";
          src: url("/fonts/Digital7.ttf") format("truetype");
        }
        .font-digital {
          font-family: "Digital", monospace;
        }
      `}</style>
    </div>
  );
}

export default Elimination;
