const renderDiamondGrid = () => {
  return (
    <div className="relative w-full h-[80vh] flex items-center justify-center">
      {users.map((user) => (
        <div
          key={user.id}
          className={`absolute transform rotate-45 w-32 h-32 bg-cover bg-center cursor-pointer transition-all duration-300 ease-in-out ${
            eliminatedUsers[user.id] ? 'opacity-50' : 'opacity-100'
          }`}
          style={{
            backgroundImage: user.photo
              ? `url(https://ybg-back.vercel.app/user/${user.id}/photo)`
              : 'none',
            top: calculatePosition(user.player_number, users.length).top,
            left: calculatePosition(user.player_number, users.length).left,
          }}
          onClick={() => toggleElimination(user)}
        >
          <div className="absolute bottom-0 right-0 font-digital text-green-900 p-1 rounded-full w-8 h-8 flex items-center justify-center">
            {user.player_number}
          </div>
        </div>
      ))}
    </div>
  );
};
