import React, { useEffect, useState, useRef } from "react";
import sound from "../sound.MP3";

function Elimination() {
  // All existing state and functions remain the same
  const [users, setUsers] = useState([]);
  const audioRefs = useRef({});
  const [eliminatedUsers, setEliminatedUsers] = useState({});
  const commonSoundRef = useRef(null);
  const [screenWidth, setScreenWidth] = useState(window.innerWidth);

  // Existing useEffect hooks remain the same
  useEffect(() => {
    const handleResize = () => {
      setScreenWidth(window.innerWidth);
    };

    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await fetch("https://ybg-back.vercel.app/elimination");
        const data = await response.json();
        setUsers(data.users);
      } catch (error) {
        console.error("Error fetching users:", error);
      }
    };

    fetchUsers();
  }, []);

  const toggleElimination = (user) => {
    setEliminatedUsers((prev) => ({
      ...prev,
      [user.id]: !prev[user.id],
    }));

    if (!eliminatedUsers[user.id] && commonSoundRef.current) {
      commonSoundRef.current.currentTime = 0;
      commonSoundRef.current.play();
    }

    const userAudioElement = audioRefs.current[user.id];
    if (userAudioElement) {
      setTimeout(() => {
        if (!eliminatedUsers[user.id]) {
          userAudioElement.currentTime = 0;
          userAudioElement.play().catch((error) => {
            console.error("Error playing user-specific audio:", error);
          });
        }
      }, 1000);
    }
  };

  const renderDiamondGrid = () => {
    const sortedUsers = [...users].sort(
      (a, b) => a.player_number - b.player_number
    );

    const diamondSize = 7 * 16;
    const rowCapacity = Math.floor(screenWidth / (diamondSize + 16));
    const rows = [];
    let toggle = true;
    let index = 0;

    while (index < sortedUsers.length) {
      const rowCount = toggle ? rowCapacity : rowCapacity - 1;
      const rowUsers = sortedUsers.slice(index, index + rowCount);
      rows.push(rowUsers);
      index += rowCount;
      toggle = !toggle;
    }

    return (
      <div className="grid-container">
        <div className="circle-decoration left"></div>
        <div className="circle-decoration right"></div>
        {rows.map((row, rowIndex) => (
          <div
            key={rowIndex}
            className={`row ${rowIndex % 2 === 0 ? "even-row" : "odd-row"}`}
          >
            {row.map((user) => (
              <div
                key={user.id}
                className={`diamond ${
                  eliminatedUsers[user.id] ? "eliminated" : ""
                }`}
                onClick={() => toggleElimination(user)}
              >
                <img
                  src={`https://ybg-back.vercel.app/user/${user.id}/photo`}
                  alt={`Player ${user.player_number}`}
                  className="diamond-image"
                />
                <div className="player-number">
                  {String(user.player_number).padStart(3, "0")}
                </div>
                {user.audio && (
                  <audio
                    ref={(el) => (audioRefs.current[user.id] = el)}
                    src={`https://ybg-back.vercel.app/user/${user.id}/audio`}
                  />
                )}
              </div>
            ))}
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="bg-black min-h-screen text-white p-8">
      <div className="page-container">
        <div className="triangle-decoration"></div>
        <div className="square-decoration"></div>
        <h1 className="text-4xl text-red-500 text-center mb-8 uppercase tracking-widest font-squid">
          Elimination Board
        </h1>
        {renderDiamondGrid()}
        <audio ref={commonSoundRef} src={sound} />
      </div>
      <style jsx>{`
        .page-container {
          position: relative;
          min-height: 100vh;
          overflow: hidden;
          background: radial-gradient(circle at center, #1a1a1a 0%, #000000 100%);
        }

        .triangle-decoration {
          position: fixed;
          top: 20px;
          left: 20px;
          width: 60px;
          height: 60px;
          border: 3px solid #ff0066;
          clip-path: polygon(50% 0%, 0% 100%, 100% 100%);
          opacity: 0.6;
          z-index: 1;
        }

        .square-decoration {
          position: fixed;
          top: 20px;
          right: 20px;
          width: 60px;
          height: 60px;
          border: 3px solid #ff0066;
          opacity: 0.6;
          z-index: 1;
        }

        .circle-decoration {
          position: fixed;
          width: 60px;
          height: 60px;
          border: 3px solid #ff0066;
          border-radius: 50%;
          opacity: 0.6;
          z-index: 1;
        }

        .circle-decoration.left {
          bottom: 20px;
          left: 20px;
        }

        .circle-decoration.right {
          bottom: 20px;
          right: 20px;
        }

        .grid-container {
          display: flex;
          flex-direction: column;
          align-items: center;
          gap: -2rem;
          padding: 3rem;
          position: relative;
          z-index: 2;
        }

        .row {
          display: flex;
          justify-content: center;
          gap: 0.5rem;
        }

        .odd-row {
          gap: 0.5rem;
        }

        .diamond {
          margin-top: -3rem;
          width: 7rem;
          height: 7rem;
          clip-path: polygon(50% 0, 100% 50%, 50% 100%, 0 50%);
          background: rgba(255, 20, 147, 0.3);
          position: relative;
          cursor: pointer;
          transition: all 0.3s ease-in-out;
          border: 2px solid rgba(255, 0, 102, 0.3);
        }

        .diamond:hover {
          transform: scale(1.05);
          filter: brightness(1.2);
          border-color: rgba(255, 0, 102, 0.8);
        }

        .diamond.eliminated {
          opacity: 0.15;
          filter: grayscale(100%);
        }

        .diamond-image {
          width: 100%;
          height: 100%;
          object-fit: cover;
          clip-path: polygon(50% 0, 100% 50%, 50% 100%, 0 50%);
          filter: contrast(1.2) brightness(0.8);
        }

        .player-number {
          position: absolute;
          bottom: 10%;
          left: 50%;
          transform: translateX(-50%);
          font-size: 0.8rem;
          color: #00ff00;
          text-shadow: 0 0 8px rgba(0, 255, 0, 0.8),
            0 0 12px rgba(0, 255, 0, 0.6);
          font-family: "Digital";
          font-weight: bold;
        }

        h1 {
          position: relative;
          z-index: 2;
          text-shadow: 0 0 20px rgba(255, 0, 102, 0.5);
        }

        @keyframes pulse {
          0% { opacity: 0.6; }
          50% { opacity: 0.8; }
          100% { opacity: 0.6; }
        }

        .triangle-decoration,
        .square-decoration,
        .circle-decoration {
          animation: pulse 2s infinite ease-in-out;
        }
      `}</style>
    </div>
  );
}

export default Elimination;
